    /**
     * Custom DML update/insert/delete logic here.
     * @param operation the operation type
     * @param e the transaction event
     */
    protected void doDML(int operation, TransactionEvent e) {
        
        if (operation==DML_INSERT) {
            ApplicationModule am=this.getDBTransaction().getRootApplicationModule();
            ViewObject        vo=am.findViewObject("mySrvDapPK");
            if (vo!=null)
                {
                   vo.remove();     
                }
            vo=am.createViewObjectFromQueryStmt("mySrvDapPK",
                                                "Select 2||'"+getLocationid()+"'||'"+DigicomClass.doGetFormattedDate(getDapDate().toString(), "yyMM")+"'||Lpad(nvl(max(to_number(substr(DAP_ID,-4)))+1,1) ,4,'0') as PK \n"+
                                                "from SRV_DAP \n"+
                                                "Where to_char(Dap_Date,'rrmm') ='"+ DigicomClass.doGetFormattedDate(getDapDate().toString(), "yyMM")+"' \n"+
                                                "and locationid = '"+getLocationid()+"'");
            vo.executeQuery();
            setDapId(vo.first().getAttribute(0).toString());
            
            getAccAllStoresDapStore().getViewObject().setWhereClause("");
            getAccAllStoresDapStore().getViewObject().executeQuery();
            getAccAllStoresDapStore().getViewObject().setWhereClause("upper(store_name) like '%DAP%'");
            getAccAllStoresDapStore().getViewObject().executeQuery();
            //--setStoreid(getAccAllStoresDapStore().first().getAttribute(Storeid));
            setStoreid(getAccAllStoresDapStore().first().getAttribute("Storeid").toString());
            if (vo!=null) {
                vo.remove();
           }                
            vo=am.createViewObjectFromQueryStmt("mySrvDapPK", "SELECT CUSTOMERID FROM ALL_CUSTOMERS WHERE IS_DAP_CUSTOMER='Y' AND LOCATIONID='"+getLocationid()+"'");
            vo.executeQuery();
            setCustomerid(vo.first().getAttribute(0).toString());
            if (vo!=null) {
                vo.remove();
            }
            vo=am.createViewObjectFromQueryStmt("mySrvDapPK", "SELECT SALESPERSONID,SALESTERRITORYID,ID  FROM ALL_CUSTOMER_SALESPERSON WHERE DEFAULT_PERSON='Y' AND CUSTOMERID='"+getCustomerid()+"'");
            vo.executeQuery();
            setSalespersonid(vo.first().getAttribute(0).toString());
            setSalesterritoryid(vo.first().getAttribute(1).toString());
            setPid(Integer.parseInt(vo.first().getAttribute(2).toString()));
        }
        super.doDML(operation, e);
    }
